let swiper=new Swiper(".mySwiper",{slidesPerView:3,spaceBetween:30,autoplay:!0,loop:!0,pagination:{el:".swiper-pagination",clickable:!0}});function switchTab(tabName){const forms=document.getElementsByClassName("form");for(let i=0;i<forms.length;i++)forms[i].classList.remove("show");const tab=document.getElementById(`${tabName}Form`);tab.classList.add("show")}async function signup(){const username=document.getElementById("signupUsername").value,email=document.getElementById("signupEmail").value,password=document.getElementById("signupPassword").value,passwordConfirm=document.getElementById("signupPasswordConfirm").value;if(password!==passwordConfirm)return void alert("Passwords do not match.");const response=await fetch("http://localhost:8080/signup",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:username,email:email,password:password})}),data=await response.json();alert(data.message)}